package com.fred.adventofcode

fun main() {
    val input =
        """"""

    val index = input
        .windowed(4)
        .mapIndexed { a, b -> a + 4 to b }
        .toMap()
        .filter { it.value.foo() }
        .firstNotNullOf { it.key }

    println(index)
}

fun String.foo() = this.map { this.count { c -> c == it } }.sum() == 4
